mapper.select:
select oa_process.*from oa_process inner join (
select
max(f_version) as f_version,process_bid from oa_process where process_bid=#{processBid}
GROUP BY process_bid
) as processdata on processdata.process_bid=oa_process.process_bid and
Oa_process.f_version=processdata.f_version

select oa_process.*from oa_process inner join (
select
max(f_version) as f_version,process_bid from oa_process where processcode=#{processCode}
GROUP BY process_bid
) as processdata on processdata.process_bid=oa_process.process_bid and
oa_process.f_version=processdata.f_version
